script:
$(function () {
    $('##ElementID#').highcharts({
        chart: {
            type: 'area'
        },
        title: {
            text: '#Title#'
        },
        xAxis: {
            categories: [#ChartxAxisValues#],
            crosshair: true
        },
        yAxis: {
            title: {
                text: 'Amount'
            }
        },
        tooltip: {
            pointFormat: '{series.name} {point.y:,.0f}'
  },
  series: [#NAMEVALUEPAIRS#],
        exporting: {
            menuItemDefinitions: {
                fullscreen: {
                    onclick: function () {
                              $('##ElementID#').parent().toggleClass('chart-modal');
                              $('##ElementID#').highcharts().reflow();
                    },
                    text: 'Full Screen'
                }
            },
            buttons: {
                contextButton: {
                    menuItems: ['fullscreen','printChart','separator','downloadPNG','downloadJPEG','downloadPDF','downloadSVG']
                }
            }
        }
  });
  });
