script:
$(function () {
$('##ElementID#').highcharts({
  chart : {
    type: 'boxplot'
  },  
  title: {
    text: '#Title#'
  },
  xAxis:{
      },
  yAxis:{
          min:#YMIN#,
          max:#YMAX#
      },
   series: [#SERIESVALUE#],
      exporting: {
          menuItemDefinitions: {
              fullscreen: {
                  onclick: function () {
                            $('##ElementID#').parent().toggleClass('chart-modal');
                            $('##ElementID#').highcharts().reflow();
                  },
                  text: 'full screen'
              }
          },
          buttons: {
              contextButton: {
                  menuItems: ['fullscreen','printChart','separator','downloadPNG','downloadJPEG','downloadPDF','downloadSVG']
              }
          }
      }
});
});
