script:
  $(function () {
  $('##ElementID#').highcharts({
    chart : {
      type: 'boxplot'
    },  
    title: {
      text: '#Title#'
    },
    boost: {
        useGPUTranslations: true,
        usePreallocated: true,
        seriesThreshold: 1
    },
    xAxis:{
        },
    yAxis:{
        },
     series: [{
                name: '#BOXNAME#',
                data: #BOXDATA#,
                tooltip: {
                    headerFormat: '{point.key}
'
                }
            },
            {
                name: 'Outlier',
                type: 'scatter',
                data: #OUTLIERDATA#,
                marker: {
                    lineWidth: 1,
                    radius: 2
                },
                tooltip: {
                    headerFormat: '',
                    pointFormat: "{point.y}"
                },
                boostThreshold: 100,
                turboThreshold:100
            }],
        exporting: {
            menuItemDefinitions: {
                fullscreen: {
                    onclick: function () {
