script:
 $(function () {

     $('##ElementID#').highcharts({
       chart:{
         type: 'scatter'
       },
       title:{
         text:'#Title#'
       },
       xAxis: {
           crosshair: true,
           type : 'datetime',
           title:{
             text: ''
           }
       },
       yAxis: {
                 title: {
                     text: 'Amount'
                 },
                 min: 0,
                 max: #AmountMAX#
           }, 
       plotOptions: {
           scatter:{
               marker: {
                   radius: 5,
                   states: {
                       hover: {
                           enabled: true,
                           lineColor: 'rgb(100,100,100)'
                       },
                   width:15,
                   height:15
                   }
               },
               states: {
                   hover: {
                       marker: {
                           enabled: true
                       }
                   }
               }
            }
       },
       legend:{
         itemStyle:{
           fontSize:'10px'
         }
       },
       tooltip: {
           useHTML: true,
           formatter: function() {
                   var mydate = moment(this.x).format('YYYY-MM-DD');
                   return '' +this.series.name+ '
' + mydate + ' ' + this.y;
           }
       },
       series:[#DATA#],
       exporting: {
           menuItemDefinitions: {
               fullscreen: {
                   onclick: function () {
                             $('##ElementID#').parent().toggleClass('chart-modal');
                             $('##ElementID#').highcharts().reflow();
                   },
                   text: 'Full Screen'
               },
               datalabel: {
                       onclick: function () {
                           var labelflag = !this.series[0].options.dataLabels.enabled;
                           $.each(this.series, function (idx, val) {
                               var opt = val.options;
                               opt.dataLabels.enabled = labelflag;
                               val.update(opt);
                           })
                       },
                       text: 'Data Label'
                   },
                   copycharts: {
                           onclick: function () {
                               var svg = this.getSVG({
                                   chart:{
                                       width: this.chartWidth,
                                       height: this.chartHeight
                                   }
                               });
                               var c = document.createElement('canvas');
                               c.width = this.chartWidth;
                               c.height = this.chartHeight;
                               canvg(c, svg);
                               var dataURL = c.toDataURL("image/png");
                               //var imgtag = '';

                               var img = new Image();
                               img.src = dataURL;

                               copyImgToClipboard(img);
                           },
                           text: 'copy 2 clipboard'
                       }
           },
           buttons: {
               contextButton: {
                   menuItems: ['fullscreen','datalabel','copycharts','printChart','separator','downloadPNG','downloadJPEG','downloadPDF','downloadSVG']
               }
           }
       }
     });
 });
