script:
$(function () {
    $('##ElementID#').highcharts({
        chart: {
            plotBackgroundColor: null,
            plotBorderWidth: null,
            plotShadow: false,
            type: 'pie'
        },
        title: {
            text: '#Title#'
        },
        tooltip: {
            pointFormat: '{series.name}: {point.percentage:.1f}%'
        },
        plotOptions: {
            pie: {
                allowPointSelect: true,
                cursor: 'pointer',
                dataLabels: {
                    enabled: true,
                    format: '{point.name}: {point.percentage:.1f} %',
                    style: {
                        color: (Highcharts.theme && Highcharts.theme.contrastTextColor) || 'black'
                    }
                }
            }
        },
        series: [{
            name: '#SERIESNAME#',
            colorByPoint: true,
            data: [#NAMEVALUEPAIRS#]
        }],
        exporting: {
            menuItemDefinitions: {
                fullscreen: {
                    onclick: function () {
                              $('##ElementID#').parent().toggleClass('chart-modal');
                              $('##ElementID#').highcharts().reflow();
                    },
                    text: 'Full Screen'
                },
                datalabel: {
                        onclick: function () {
                            var labelflag = !this.series[0].options.dataLabels.enabled;
                            $.each(this.series, function (idx, val) {
                                var opt = val.options;
                                opt.dataLabels.enabled = labelflag;
                                val.update(opt);
                            })
                        },
                        text: 'Data Label'
                    },
                    copycharts: {
                            onclick: function () {
                                var svg = this.getSVG({
                                    chart:{
                                        width: this.chartWidth,
                                        height: this.chartHeight
                                    }
                                });
                                var c = document.createElement('canvas');
                                c.width = this.chartWidth;
                                c.height = this.chartHeight;
                                canvg(c, svg);
                                var dataURL = c.toDataURL("image/png");
                                //var imgtag = '';

                                var img = new Image();
                                img.src = dataURL;

                                copyImgToClipboard(img);
                            },
                            text: 'copy 2 clipboard'
                        }
            },
            buttons: {
                contextButton: {
                    menuItems: ['fullscreen','datalabel','copycharts','printChart','separator','downloadPNG','downloadJPEG','downloadPDF','downloadSVG']
                }
            }
        }
    });
});
