script:
$(function () {

    var tooltiparray1=[#FINALTOOLTIP#];

    $('##ElementID#').highcharts({
        title: {
            text: '#Title#'
        },
        chart:
        {
          alignTicks: false
        },
        xAxis: {
            categories: [#ChartxAxisValues#],
            crosshair: true,
            title:{
              text: '#XAxisTitle#'
            }
        },
        yAxis: [
            {
            title: {
                text: 'Percent'
            },
            min: 0,
            max: 100,
            gridLineWidth: 1,
            tickPixelInterval: 36
          },
          {
            title: {
                text: 'Amount'
            },
            min: 0,
            max: #AmountMAX#,
            opposite: true,
            gridLineWidth: 0
        }],
        plotOptions: {
            column: {
                pointPadding: 0.2,
                borderWidth: 0
            }
        },
				tooltip: {
            		useHTML: true,
      					enabled: true,
                formatter: function(args) {
                  var this_point_index = this.point.x;

                    if(this.series.name.indexOf("yield") != -1)
                    {
                       return tooltiparray1[this_point_index];
                    }
                    else
                    {
                      return this.series.name + ' ' + this.point.y;
                    }

                  }
        			},
        series:[#series#],
        exporting: {
            menuItemDefinitions: {
                fullscreen: {
                    onclick: function () {
                              $('##ElementID#').parent().toggleClass('chart-modal');
                              $('##ElementID#').highcharts().reflow();
                    },
                    text: 'Full Screen'
                }
            },
            buttons: {
                contextButton: {
                    menuItems: ['fullscreen','printChart','separator','downloadPNG','downloadJPEG','downloadPDF','downloadSVG']
                }
            }
        }
    });
});
